export default class OADBManager {
  constructor(databases) {
    /**
     * databases map
     */
    this.databases = new Map();
    if (databases) {
      for (const database of databases) {
        this.init(database);
        this.databases.set(database, new OADB(database));
      }
    }
  }

  init(database) {
    this.databases.set(database, new OADB(database));
  }

  /**
   * @param {string|undefined} database
   * @returns {OADB} - database or databases when undefined
   */
  get(database) {
    if (!database) return this.databases;
    let db = this.databases.get(database);
    if (!db) {
      this.init(database);
      db = this.databases.get(database);
    }
    return db;
  }

  /**
   * @returns {OADB} - database
   */
  set(database) {
    let db = this.databases.get(database);
    if (!db) {
      this.init(database);
      db = this.databases.get(database);
    }
    if (db) return db;
    return null;
  }
}

