    const online = this.isOnline();
    if (online) {
      if (child) return firebase.database().ref(`${this.name}/${child}`).set(value);
      else return firebase.database().ref(`${this.name}`).set(value);
    }
    if (!online && child) {
      return await set(child, value, this.localStore);
    }
    const promises = [];

    for (const key of Object.keys(value)) {
      promises.push(set(key, value[key], this.localStore))
    }
    return Promise.all(promises);
  }

  get(child) {
    return new Promise(async (resolve, reject) => {
      const online = this.isOnline();
      console.log(child);
      let data;
      if (child) data = await get(child, this.store);
      else {
        const dataKeys = await keys(this.store);
        if (dataKeys && dataKeys.length > 0) for (const key of dataKeys) {
          data[key] = get(key, this.store)
        }
      }
      if (data) resolve(data);
      if (online && this.ref) {
        let snap;
        if (child) snap = await firebase.database().ref(`${this.name}/${child}`).once('value');
        else snap = await this.ref.once('value');
        snap = snap.val();
        if (!data && snap) {
          resolve(snap);
          if (child) await set(child, snap, this.store);
          else {
            for (const key of Object.keys(snap)) {              
              await set(key, snap[key], this.store);
            }
          }
        } else if (snap && data && data.timestamp < snap.timestamp) {
            if (child) await set(child, snap, this.store);
            else {
              for (const key of Object.keys(snap)) {              
                await set(key, snap[key], this.store);
              }
            }
            document.dispatchEvent(new CustomEvent('storage-update', { detail: { child, snap } }));
          }
        }

      resolve();
    });
  }
}

export default define(class SelectableInput extends ElementBase {
  static get observedAttributes() {
    return ['type', 'name', 'placeholder'];
  }

  set opened(value) {
    if (value) {
      const { left, right, top, bottom } = this.getBoundingClientRect();
      const isMobile = window.innerWidth < 640;
      if (isMobile) {
        this.selector.style.left = '50%';
        this.selector.style.top = '50%';
        this.selector.style.transform = 'translate(-50%, -50%)';
      } else {
        this.selector.style.left = `${left}px`;
        this.selector.style.top = `${top}px`;
        this.selector.style.transform = '';
      }

      this.setAttribute('opened', '');
      this._dropDownEl.setAttribute('icon', 'arrow-drop-up');
    } else {
      this.removeAttribute('opened');
      this._dropDownEl.setAttribute('icon', 'arrow-drop-down');
    }
  }
  get opened() {
    return this.hasAttribute('opened');
  }
  get _inputEl() {
    return this.shadowRoot.querySelector('custom-input');
  }

  get _dropDownEl() {
    return this.shadowRoot.querySelector('.drop');
  }
  constructor() {
    super();
    this._onDropDownClick = this._onDropDownClick.bind(this);
  }

  get selector() {
    return this.shadowRoot.querySelector('custom-selector');
  }

  add(el) {
    this.selector.appendChild(el);
  }

  remove(el) {
    this.selector.removeChild(el);
  }

  attributeChangedCallback(name, old, value) {
    if (this.rendered && old !== value) this._inputEl.setAttribute(name, value);
  }

  connectedCallback() {
    if (super.connectedCallback) super.connectedCallback();
    this._inputEl.setAttribute('name', this.getAttribute('name'));
    this._inputEl.setAttribute('type', this.getAttribute('type') || 'text');
    this._inputEl.setAttribute('placeholder', this.getAttribute('placeholder'));
    this._dropDownEl.addEventListener('click', this._onDropDownClick);
  }

  _onDropDownClick() {
    this.opened = !this.opened;
  }

  get template() {
    return html `
<style>
  :host {
    mixin(--css-column)
    width: 100%;
  }
  custom-selector {
    mixin(--css-column)
    position: fixed;
