    window.shoppingCart.add = this.add.bind(this);
  }
  
  connectedCallback() {
    this.shadowRoot.innerHTML = `
      <style>
        :host {
          display: flex;
          flex-direction: column;
          height: 56px;
          max-width: calc(72px * 3);
          padding: 16px;
          box-sizing: border-box;
          background: #fff;
          z-index: 100;
