        .row {
          display: flex;
          align-items: center;
        }
        .actions-container {
          display: flex;
          flex-direction: row;
          height: 56px;
          max-width: calc(72px * 3);
          min-height: 56px;
        }
        :host(.opened) {
          top: 0;
          bottom: 0;
          left: 0;
          right: 0;
          position: fixed;
          height: 100%;
          width: 100%;
          max-width: 100%;
