customElements.define("custom-date",class CustomDate extends HTMLElement{static get observedAttributes(){return["day","month","year","date","value","lang"]}get months(){return["jan","feb","mar","apr","may","jun","jul","aug","sep","oct","nov","dec"]}get days(){return"nl"===this.lang?["zondag","maandag","dinsdag","woensdag","donderdag","vrijdag","zaterdag"]:["sunday","monday","tuesday","wednesday","thursday","friday","saturday"]}set lang(t){this._lang=t,this.render()}get lang(){return this._lang}set value(t){this._value=t,this.setAttribute("value",t);const e=new Date(Number(t));this.day=e.getDay(),this.date=e.getDate(),this.month=e.getMonth(),this.year=e.getFullYear()}set day(t){this._day=t,this.setAttribute("day",t)}set month(t){this._month=t,this.setAttribute("month",t)}set date(t){this._date=t,this.setAttribute("date",t)}set year(t){this._year=t,this.setAttribute("year",t)}get day(){return this._day}get month(){return this._month}get date(){return this._date}get year(){return this._year}attributeChangedCallback(t,e,a){e!==a&&a&&(this[t]=a,this.observer())}next(t){"string"==typeof t&&(t=this.days.indexOf(t));const e=new Date(Number(this._value));t===e.getDay()?this.value=e.setDate(e.getDate()-e.getDay()+7+t):this.value=e.setDate(e.getDate()-e.getDay()+t)}observer(){this.day&&this.date&&this.month&&this.year&&this.render()}render(){this.shadowRoot.innerHTML=`\n    <style>\n      :host {\n        display: flex;\n        width: 140px;\n      }\n      .flex {\n        flex: 1;\n      }\n      :host div, :host span {\n        pointer-events: none;\n      }\n    </style>\n    <div>${this.days[this.day]}</div>\n    <span class="flex"></span>\n    <div>${this.date}</div>\n    -\n    <div>${Number(this.month)+1}</div>\n    -\n    <div>${this.year}</div>\n    `}constructor(){super(),this.attachShadow({mode:"open"})}connectedCallback(){this.getAttribute("value")||(this.value=(new Date).getTime())}});
