export default define(class BusyAnimation extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"}),this.shadowRoot.innerHTML=this.template}get template(){return"\n    <style>\n      :host {\n        display: block;\n        width: 40px;\n        height: 40px;\n        margin: 0 auto;\n        background-color: #333;\n\n        border-radius: 100%;\n        -webkit-animation: scale 1.0s infinite ease-in-out;\n        animation: scale 1.0s infinite ease-in-out;\n      }\n\n      @-webkit-keyframes scale {\n        0% { -webkit-transform: scale(0) }\n        100% {\n          -webkit-transform: scale(1.0);\n          opacity: 0;\n        }\n      }\n\n      @keyframes scale {\n        0% {\n          -webkit-transform: scale(0);\n          transform: scale(0);\n        } 100% {\n          -webkit-transform: scale(1.0);\n          transform: scale(1.0);\n          opacity: 0;\n        }\n      }\n    </style>\n    "}});
